mapper.select:
SELECT 

FROM sys_classinfo a

WHERE a.id = #{id}

SELECT 

FROM sys_classinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.class_num = #{classNum}


		AND a.class_name LIKE 
			'%'||#{className}||'%'
			'%'+#{className}+'%'
			concat('%',#{className},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_classinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_classinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
